package introjava;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int one;
		int two;
		
		one = 10;
		two = 20;

		
		System.out.println(one + two);
		System.out.println(one - two);
		System.out.println(one * two);
		System.out.println(one / two);
		
		
		
	}

}
